INSERT INTO recipes (title, description, ingredients, instructions, image_url)
VALUES ('Classic Margherita Pizza', 'A classic pizza made with tomato sauce, mozzarella cheese, and fresh basil', 
        '{"Pizza dough","Tomato sauce","Mozzarella cheese","Fresh basil"}',
        '{"1. Preheat the oven to 4Q50°F.","2. Roll out the pizza dough to the desired thickness and shape.","3. Spread tomato sauce over the dough leaving a small border around the edges.","4. Add the mozzarella cheese on top of the sauce.","5. Bake the pizza in the oven for 10-12 minutes or until the crust is golden brown.","6. Remove the pizza from the oven and top with fresh basil leaves."}', 
        'https://images.unsplash.com/photo-1600028068383-ea11a7a101f3?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=774&q=80');


INSERT INTO recipes (title, description, ingredients, instructions, image_url)
VALUES ('Spinach and Feta Stuffed Chicken', 
        'This recipe is an easy and flavorful way to enjoy chicken. The spinach and feta cheese stuffing adds a pop of flavor and nutrition to your meal.',
        '{"4 boneless, skinless chicken breasts","2 cups fresh spinach","1/2 cup crumbled feta cheese","1 tablespoon olive oil","1 tablespoon garlic powder","salt and pepper to taste"}', 
        '{"1. Preheat oven to 375°F.","2. In a skillet, heat olive oil over medium heat.","3. Add spinach and cook until wilted.","4. Remove from heat and stir in feta cheese.","5. Slice a pocket into each chicken breast.","6. Stuff each chicken breast with spinach and feta mixture.","7. Sprinkle garlic powder, salt, and pepper over the chicken.","8. Place chicken breasts in a baking dish and bake for 25-30 minutes, or until chicken is cooked through.',
        'https://media.istockphoto.com/id/118313946/photo/chicken-breasts-stuffed-with-spinach-and-feta.jpg?s=612x612&w=is&k=20&c=RoMZTAJvrgDQCGERiVEKmSjxoObr7CZYYoe9ZQaZz5I=');

INSERT INTO recipes (title, description, ingredients, instructions, image_url)
VALUES ('Butternut Squash Soup', 
        'This soup is perfect for a cozy night in. The creamy butternut squash base is complemented by the warm spices, making it a comforting and delicious meal.',
        '1 butternut squash, peeled and cubed\n- 1 onion, chopped\n- 2 cloves garlic, minced\n- 2 cups chicken or vegetable broth\n- 1 teaspoon ground cinnamon\n- 1/2 teaspoon ground nutmeg\n- salt and pepper to taste\n- 1/4 cup heavy cream (optional)', 
        '1. In a large pot, sauté onion and garlic over medium heat until fragrant. \n2. Add butternut squash and broth to the pot. \n3. Bring to a boil, then reduce heat and simmer for 20-25 minutes, or until the squash is tender. \n4. Add cinnamon, nutmeg, salt, and pepper to the pot. \n5. Use an immersion blender to blend soup until smooth. \n6. Add heavy cream if desired and stir until combined. \n7. Serve hot.',
        'https://images.unsplash.com/photo-1602502673082-514350e8a58d?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60')
